    <div id='projects'>
        <h1>Projects</h1>
        <div class="project-grid">
            {#each projects as project}
                <div class="project-card">
                    <img src={project.image} alt={project.name}>
                    <h3>{project.name}</h3>
                    <p>{project.description}</p>
                </div>
            {/each}
    </div>
</section>
<style>
    *{
        box-sizing: border-box;
    }
    section{
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
    }

    div {
        margin: 1em;
        padding: 3em;
        
        background-color: #f8f8f8;
        border-radius: 2em;
        text-wrap: wrap;
    }

    .normal, #projects {
        width: 50%;
    }


    span {
        color: #5271ff;
    }

    svg {
        margin: 1.25em;
        transition: 0.3s;
    }

    svg:hover {
        transform: translateY(-7px);
    }

    h3{
        text-decoration: underline;
    }

    .project-grid {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    gap: 2em;
  }

  .project-card {
    background-color: #fff;
    border-radius: 1em;
    padding: 1.5em;
    width: 500px;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    transition: transform 0.2s ease-in-out;
  }

  .project-card:hover {
    transform: translateY(-5px); /* Small lift effect on hover */
  }

  img {
    width: 100%;
    height: auto;
    object-fit: contain;
    border-radius: 0.5em;
    margin-bottom: 1em;
  }

  .project-card > h3 {
    color: #5271ff;
    margin-bottom: 0.5em;
  }
</style>